import React, { useState, useEffect } from 'react';
import QuestionCard from './components/QuestionCard'; // Importa il componente QuestionCard
import { questions } from './data/questions'; // Importa le domande

const TOTAL_QUESTIONS = questions.length; // Numero totale di domande

const App: React.FC = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Indice della domanda corrente
  const [score, setScore] = useState(0); // Punteggio
  const [gameOver, setGameOver] = useState(false); // Indica se il quiz è terminato

  const startQuiz = () => {
    setGameOver(false); // Inizia il quiz
  };

  const handleAnswerClick = (answerIndex: number) => {
    const isCorrect = answerIndex === questions[currentQuestionIndex].correctAnswer;

    setScore((prevScore) => (prevScore + (isCorrect ? 1 : 0)));
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % TOTAL_QUESTIONS);

    if (currentQuestionIndex === TOTAL_QUESTIONS - 1) {
      setGameOver(true); // Mostra la schermata finale
    }
  };

  return (
    <div className="App">
      <h1>Quiz sul Turismo Sostenibile</h1>
      <p>Metti alla prova le tue conoscenze!</p>
      {!gameOver && (
        <QuestionCard
          questionNumber={currentQuestionIndex + 1} // Numero della domanda
          totalQuestions={TOTAL_QUESTIONS} // Numero totale di domande
          question={questions[currentQuestionIndex].question} // Testo della domanda
          answers={questions[currentQuestionIndex].answers} // Array di risposte
          onAnswerClick={handleAnswerClick} // Funzione da richiamare al click su una risposta
        />
      )}
      {gameOver && (
        <FinalScreen
          score={score}
          skillLevel={getSkillLevel(score)} // Funzione per ottenere il livello di "skill" in base al punteggio
          onRestartClick={startQuiz} // Funzione per ricominciare il quiz
        />
      )}
    </div>
  );
};

export default App;
