export interface Answer {
    text: string;
    isCorrect: boolean;
  }

  export const questions: { question: string; answers: Answer[]; correctAnswer: string; }[] = [
    {
      question: "Quale di queste NON è una caratteristica del turismo sostenibile?",
      answers: [
        { text: "Parigi", isCorrect: true },
        { text: "Londra", isCorrect: false },
        { text: "Berlino", isCorrect: false },
        { text: "Roma", isCorrect: false },
      ],
      correctAnswer: "Parigi", // Add the correct answer property
    },
    // ... other questions ...
  ];
