import React from 'react';
import './QuestionCard.css';

interface QuestionCardProps {
  questionNumber: number; // Numero della domanda (intero)
  totalQuestions: number; // Numero totale di domande (intero)
  question: string; // Testo della domanda (stringa)
  answers: string[]; // Array di stringhe con le risposte
  onAnswerClick: (answerIndex: number) => void; // Funzione chiamata quando si clicca su una risposta (prende l'indice della risposta come argomento)
}

const QuestionCard: React.FC<QuestionCardProps> = ({
  questionNumber,
  totalQuestions,
  question,
  answers,
  onAnswerClick,
}) => {
  return (
    <div className="question-card">
      <p className='question-number'>Domanda {questionNumber} di {totalQuestions}</p>
      <h3>{question}</h3>
      <ul className='ul-answers'>
        {answers.map((answer, index) => (
          <li key={index}>
            <button onClick={() => onAnswerClick(index)}>{answer}</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default QuestionCard;
